import React, {useContext} from "react";
import {Link} from "react-router-dom";
import * as R from 'ramda';

import {CurrentBasketContext} from 'contexts/currentBasket'

/**
 * Компонент basket расположен в header иконка корзины
 */
const BasketCart = () => {
    const [currentBasketState] = useContext(CurrentBasketContext)
    return (
        <div>
            <div className="topbar-cart">
                <Link to="/basket" className="icon-cart">
                    <i className="icon-basket f-15"></i>
                        <span className="count cart-count">{R.length(currentBasketState)}</span>
                    </Link>
                </div>
            </div>

    )
};



export default BasketCart;

