import React, {useContext, useState} from "react";

import {CurrentSearchContext} from 'contexts/currentSearch'
import {SEARCH_PRODUCT} from 'actionTypes'


/**
 * Компонент search. Это всплывающее модальное окно и иконка поиска в header
 */
const Search = () => {
    const [isActive, setIsActive] = useState(false)
    const [value, setValue] = useState('')
    const [, dispatch] = useContext(CurrentSearchContext)

    const handleChange = event => {
        setValue(event.target.value)
        dispatch({type: SEARCH_PRODUCT, payload: value})

    }

    const handleSubmit = event => {
        event.preventDefault();
        dispatch({type: SEARCH_PRODUCT, payload: value})
        const search = document.querySelector('.searchWindow');
        search.setAttribute('hidden', 'true');
        setValue('')
    }

    const toggleActive = () => {
       setIsActive (!isActive);
    }

    const runScript = event => {
        if(event.keyCode === 13){
            return true;
        }
        return false;
    };

    const renderSearch = () => {
        let body = document.getElementById('body');
        if(!isActive) {
            body.classList.remove('modal-open');
            return '';
        }
        body.classList.add('modal-open');


        return (
            <div className='searchWindow'>
              <div className="modal fade in" id="myModal" role="dialog" aria-hidden='false' style={{display: 'block', paddingLeft: 0 + 'px'}} >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button type="button" className="close" data-dismiss="modal" onClick={toggleActive}>&times;</button>
                            <h4 className="modal-title">Поиск</h4>
                        </div>
                        <div className="modal-body">
                            <div className="input-group" >
                                <form method="get" className="searchform"  role="search" onSubmit={runScript && handleSubmit}>
                                    <input type="hidden" name="type" value="product"/>
                                    <input type="text" name="q" className="form-control control-search" defaultValue='' onChange={handleChange}/>
                                    <span className="input-group-btn">
                              <button className="btn btn-default button_search" type="button" onClick={handleSubmit}>
                                  <i data-toggle="dropdown"
                                     className="fa fa-search">
                                  </i>
                              </button>
                            </span>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
                <div className='modal-backdrop fade in'></div>
            </div>
        )
    }

        return (
            <div>
                {isActive && renderSearch()}
               <div className="topbar-search">

                <div className="search-popup dropdown" data-toggle="modal" data-target="#myModal">
                    <a href="#"><i className="icon-magnifier f-15" onClick={toggleActive}></i></a>
                </div>
               </div>

            </div>
        )
    }

export default Search;