export const validate = (values, isLogin) => {

    let errors = {};
    if (!values.name && !isLogin) {
        errors.name = 'Введите пожалуйста имя!';
    } else if (values.name.length < 2 && !isLogin){
        errors.name = 'Ваше имя слишком короткое!';
    }
    if (!values.surname && !isLogin) {
        errors.surname = 'Введите пожалуйста фамилию!';
    } else if (values.surname.length < 2 && !isLogin){
        errors.surname = 'Ваша фамилия слишком короткое!';
    }
    if (!values.address && !isLogin) {
        errors.address = 'Введите пожалуйста адрес!';
    } else if (values.address.length < 5 && !isLogin){
        errors.address = 'Ваш адрес слишком короткий!';
    }
    if (!values.phone && !isLogin) {
        errors.phone = 'Введите пожалуйста свой телефон!';
    } else if ( (values.phone.length < 7) && !isLogin){
        errors.phone = 'Ваш телефон должен быть не меньше 7 цифр!';
    }
    if(!values.email){
        errors.email = 'Введите свою электронную почту!';
    }
    if(!values.password){
        errors.password = 'Введите пароль!';
    }else if(!/^[a-zA-Z0-9]+$/.test(values.password) ){
        errors.password = 'Пароль может содержать только цифры и латинские буквы!';
    }
    return errors;
}

export const validateOnCheckout = (values) => {
    let errors = {};
    if (!values.name) {
        errors.name = 'Введите пожалуйста имя!';
    } else if (values.name.length < 2){
        errors.name = 'Ваше имя слишком короткое!';
    }
    if (!values.address) {
        errors.address = 'Введите пожалуйста адрес!';
    } else if (values.address.length < 5){
        errors.address = 'Ваш адрес слишком короткий!';
    }
    if (!values.phone) {
        errors.phone = 'Введите пожалуйста свой телефон!';
    } else if (values.phone.length < 7){
        errors.phone = 'Ваш телефон должен быть не меньше 7 цифр!';
    }
    return errors
}
